const purgecss = require('@fullhuman/postcss-purgecss')

module.exports = {
  plugins: [
    purgecss({
      content: [
        './index.html',
        './src/**/*.vue',   // jeśli używasz Vue
        './src/**/*.js',
        './src/**/*.ts',
        './src/**/*.html',
      ],
      safelist: [
        /^btn/,       // np. btn-primary, btn-danger
        /^modal/,     // Bootstrap modale
        /^show/,      // Bootstrap + lightbox
        /^lightbox/,  // Lightbox
      ],
      defaultExtractor: content => content.match(/[\w-/:]+(?<!:)/g) || [],
    })
  ]
}
